    private void IDNumTFKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_IDNumTFKeyTyped
char c = evt.getKeyChar();
if (!(Character.isDigit(c) || (c == KeyEvent.VK_BACK_SPACE) || c == KeyEvent.VK_DELETE)){
    evt.consume();
}
if(IDNumTF.getText().length()>=13){
    evt.consume();
}

    }//GEN-LAST:event_IDNumTFKeyTyped

