    private void OkayButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_OkayButtonActionPerformed
        
        /*****************Get Age from ID*****************/
        
        int year = Calendar.getInstance().get(Calendar.YEAR);
        int before2000 = 1900;
        int after2000 = 2000;
        
        String IDNUMBER = IDNumTF.getText();
        String FirstTwoNumbersOfIDNum = IDNUMBER.substring(0, 2);
        int StudentIDYear = Integer.parseInt(FirstTwoNumbersOfIDNum);
        int Age = year - StudentIDYear;
        
        if (StudentIDYear <= 99) {
            Age = year - (StudentIDYear + before2000);
            System.out.println(Age);
        }

        if (StudentIDYear <= 19) {
            Age = year - (StudentIDYear + after2000);
            System.out.println(Age);
        }

        if (StudentIDYear <= 9) {
            Age = year - (StudentIDYear + after2000);
        }

        
        /*****************Get Date Of Birth from ID*****************/
        String FirstSixNumbersOfIDNum = IDNUMBER.substring(0,6);
        StringBuilder strSix = new StringBuilder(FirstSixNumbersOfIDNum);

        strSix.insert(2, '-' );
        strSix.insert(5, '-' );
        
        
            if (strSix.charAt(0) == '0' || strSix.charAt(0) == '1') {
            strSix.insert(0, "20" );
            }
        
        
            else
            strSix.insert(0, "19" );
            
        
            
        /*****************Get Gender*****************/
            
            
            
            String NumbersOfIDNum = IDNUMBER.substring(6,10);
            int intNumbersOfIDNum=Integer.parseInt(NumbersOfIDNum);
            String Gender = "";
            
            if (intNumbersOfIDNum > 0000 && intNumbersOfIDNum < 4999 ){
                Gender = "Female";
            }
            
            if (intNumbersOfIDNum > 4999 && intNumbersOfIDNum < 9999 ){
                Gender = "Male";
            }
                
            
        /*****************SA Citizen *****************/     
            String Number11OfIDNum = IDNUMBER.substring(10,11);
            int intNumber11OfIDNum = Integer.parseInt(Number11OfIDNum);
            String Citizen = "";
            
            if (intNumber11OfIDNum == 0 ){
                Citizen = "SA Citizen";
            }
            
            if (intNumber11OfIDNum == 1 ){
                Citizen = "Permanent Resident";
            }
            
            if (intNumber11OfIDNum > 1 ){
                Citizen = "Not South African";
            }

        /*****************Valid ID *****************/ 
  
        String IDValid = IDNUMBER.replaceAll("(.{1})", "$1,");       //  9,0,0,3,2,0,5,1,1,8,0,8,1,
//        String [] SplitID = IDValid.split(",");                      
//        String  ArraySplitIDToString = Arrays.toString(SplitID);    //  [9,0,0,3,2,0,5,1,1,8,0,8,1]
//        Integer result = Integer.valueOf(IDNUMBER);
//        int[] array = Arrays.asList(IDValid).stream().mapToInt(Integer::parseInt).toArray();
        
        for(int i = 0; i < IDNUMBER.length() ;
        i++)
            
            if (i % 2 == 1) {
                IDNUMBER.substring(i, i + 1);
            }
            
        
            
            
            
            
            
            
        //9003205118081  



        info.setText("Student Info : " + NameTF.getText() + " " +SurnameTF.getText() +" " + EnrolmentCB.getSelectedItem());
        AgeLabel.setText("Age : " + Age);
        DateOfBirthLabel.setText("Date of Birth : " + strSix) ;
        GenderLabel.setText("Gender : " + Gender );
        CitizenLabel.setText("SA Citizen : " + Citizen );
        ValidLabel.setText("Valid ID: " + IDNUMBER );
        
    }//GEN-LAST:event_OkayButtonActionPerformed

